mapper.update:
update user_party
set is_group=1
where party_id = #{partyId}

update user_party
set is_active=0
where party_id = #{partyId}

mapper.delete:
delete
from user_party
where party_id = #{partyId}
  and user_id = #{userId}

mapper.select:
select count(*)
from user_party
where party_id = #{partyId}
